import React, { Fragment, useState} from "react";
import { useForm, ErrorMessage } from "react-hook-form";

import Names from "./Names";
import Address from "./Address";
import Contact from "./Contact";
import Review from "./Review";
import Submit from "./Submit";

const steps = [
  { id: "names" },
  { id: "address" },
  { id: "contact" },
  { id: "review" },
  { id: "submit" }
];

const defaultValues = {
  firstName: "Jane",
  lastName: "Doe",
  nickName: "Jan",
  address: "200 South Main St",
  city: "Anytown",
  state: "CA",
  zip: "90505",
  email: "email@domain.com",
  phone: "+61 4252 454 332"
};

const MultipleStepForm = () => {
  const { register, handleSubmit, setValue } = useForm({
    defaultValues: defaultValues
  });
  const [ step, setStep ] = useState('Name');
  const [ nameData, setNameData ] = useState({
      firstName: "Jane",
      lastName: "Doe",
      nickName: "Jan"
  })

  const [ addressData, setAddressData ] = useState({
    address: "200 South Main St",
    city: "Anytown",
    state: "CA",
    zip: "90505"
  })

  const [ contactData, setContactData ] = useState({
    email: "email@domain.com",
    phone: "+61 4252 454 332"
  })

  console.log(step)
  console.log(nameData)
  console.log(addressData)
  console.log(contactData)

  return (
    <Fragment>
      { step === 'Name' && <Names {...[setNameData, setStep]} /> }
      { step === 'Address' && <Address {...[setAddressData, setStep]} /> }
      { step === 'Contact' && <Contact {...[setContactData, setStep]} /> }
      { step === 'Review' && <Review /> }
    </Fragment>
  );
};

export default MultipleStepForm;
