import React, { Fragment } from "react";
import { useForm, ErrorMessage } from "react-hooks-form";

import Names from "./Names";
import Address from "./Address";
import Contact from "./Contact";
import Review from "./Review";
import Submit from "./Submit";

const steps = [
  { id: "names" },
  { id: "address" },
  { id: "contact" },
  { id: "review" },
  { id: "submit" }
];

const defaultData = {
  firstName: "Jane",
  lastName: "Doe",
  nickName: "Jan",
  address: "200 South Main St",
  city: "Anytown",
  state: "CA",
  zip: "90505",
  email: "email@domain.com",
  phone: "+61 4252 454 332"
};

const MultipleStepForm = () => {
  const [formData, setForm] = useForm(defaultData);
  const { step, setStep } = useState('Name');
  const { id } = step;
  const { register, setValue } = useForm();

  const props = { formData, setForm, navigation };

  return (
    <Fragment>
      { step === 'Name' && <Names {...props} setValue /> }
      { step === 'Address' && <Address {...props} /> }
      { step === 'Contact' && <Contact {...props} /> }
      { step === 'Review' && <Review {...props} /> }
    </Fragment>
  );
};

export default MultipleStepForm;
