import React from "react";
import Button from '@material-ui/core/Button';
import { useForm, ErrorMessage } from "react-hook-form";
import { useStateMachine } from "little-state-machine";
import updateAction from "./updateAction";
import TextField from '@material-ui/core/TextField';

const Contact = ({ setForm, formData, setStep }) => {
  const { register, handleSubmit } = useForm();
  const { action, state } = useStateMachine(updateAction);

  const onSubmit = data => {
    action(data)
    setStep('Address')
  };

  return (
    <div className="form">
      <h3>Contact </h3>
      <TextField label="Phone" name="phone" value={state.data.phone} />
      <TextField label="E-mail" name="email" value={state.data.email} />
      <div>
        <Button onClick={setStep('Address')}>Previous</Button>
        <Button onClick={setStep('Review')}>Next</Button>
      </div>
    </div>
  );
};

export default Contact;
