import React from "react";
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { useForm, ErrorMessage } from "react-hook-form";
import { useStateMachine } from "little-state-machine";
import updateAction from "./updateAction";
import StateDrop from "./StateDrop";

const Address = ({ setStep }) => {
  const { register, handleSubmit } = useForm();
  const { action, state } = useStateMachine(updateAction);

  const onSubmit = data => {
    action(data)
    setStep('Address')
  };

  return (
    <div className="form">
      <h3>Address</h3>
      <TextField
        label="Address"
        name="address"
        value={state.data.address}
        nputRef={register} 
      />
      <TextField label="City" name="city" value={state.data.city} nputRef={register}  />
      <StateDrop label="State" name="state" value={state.data.state} onputRef={register}  />
      <TextField label="Zip" name="zip" value={state.data.zip} nputRef={register}  />
      <div>
        <Button onClick={setStep('Name')}>Previous</Button>
        <Button onClick={setStep('Contact')}>Next</Button>
      </div>
    </div>
  );
};

export default Address;
